mapper.update:

UPDATE MYMEMBER
SET MEM_NAME = #{memName}, MEM_TEL = #{memTel}, MEM_ADDR = #{memAddr}
WHERE MEM_ID = #{memId}

mapper.select:

SELECT COUNT(*) cnt
FROM MYMEMBER
WHERE MEM_ID = #{memId}


SELECT MEM_ID memId, MEM_NAME memName, MEM_TEL memTel, MEM_ADDR memAddr, REG_DT regDt 
FROM MYMEMBER
WHERE MEM_ID = #{memId}


SELECT MEM_ID, MEM_NAME, MEM_TEL, MEM_ADDR, REG_DT 
FROM MYMEMBER
WHERE MEM_ID = #{memId}


SELECT MEM_ID memId, MEM_NAME memName, MEM_TEL memTel, MEM_ADDR memAddr, REG_DT regDt 
FROM MYMEMBER
WHERE MEM_ID = #{memId} AND MEM_NAME = #{memName}


SELECT MEM_ID memId, MEM_NAME memName, MEM_TEL memTel, MEM_ADDR memAddr, REG_DT regDt 
FROM MYMEMBER




		and mem_id = #{memId}



		and mem_name = #{memName}



		and mem_tel = #{memTel}



		and mem_addr like '%' || #{memAddr} || '%'
